SET NAMES 'latin1';

CREATE SCHEMA `omega` COLLATE latin1_swedish_ci;

USE `omega`;

CREATE TABLE `posts`(
  `id` INT(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `title` VARCHAR(255) NOT NULL,
  `text` TEXT NOT NULL,
  `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
)
ENGINE = InnoDB
AUTO_INCREMENT = 232
COLLATE = latin1_swedish_ci
ROW_FORMAT = COMPACT;

CREATE TABLE `users`(
  `id` INT(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `username` VARCHAR(255) NOT NULL,
  `password` VARCHAR(255) NOT NULL,
  PRIMARY KEY (`id`)
)
ENGINE = InnoDB
AUTO_INCREMENT = 2
COLLATE = latin1_swedish_ci
ROW_FORMAT = COMPACT;

